catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Type appropriate comment here, and begin script below

    // Allows formats of (999) 999-9999, 999-999-9999, and 9999999999
    /* This Regex was found on codegrepper.com and it allows international formats as well, here is the list of formats
		(123) 456-7890
		(123)456-7890
		123-456-7890
		123.456.7890
		1234567890
		+31636363634
		075-63546725
		*/
    var phoneNo = /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/im;
    // /^[(]?(\d{3})[)]?[-|\s]?(\d{3})[-|\s]?(\d{4})$/;
    //An if statement to check to see the phone number field is in the correct format
    if (!phoneNo.test(newValue)) {
        //alert('Please enter a valid phone number');
        g_form.setValue('phone_number', '');
        g_form.showFieldMsg('phone_number', 'Please enter a valid phone number', 'error');
    }

}
