sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var carService = g_form.getValue('services');

	// Clear all values from the short description field
    g_form.clearValue('short_description');

	// If brake inspection is selected the short description will be filed with a description of the brake inspection service  
	if(newValue == 'brake inspection'){
     g_form.setValue('short_description', 'Inspection of your brakes and rotors and if needed they will be changed out for new ones.');
    }

	// If fluid inspection is selected the short description will be filed with a description of the fluid inspection service  
	if(newValue == 'fluid inspection'){
     g_form.setValue('short_description', 'Inspection of your fluids and a top off if any fluids are needed.');
    }

	// If multi-point inspection is selected the short description will be filed with a description of the multi-point inspection service  
	if(newValue == 'multi-point inspection'){
     g_form.setValue('short_description', 'An inspection of the fluids and also a visual check of all major systems.');
    }

	// If oil & filter  is selected the short description will be filed with a description of the oil & filter service  
	if(newValue == 'oil & filter'){
     g_form.setValue('short_description', 'This is a complete oil change with filter included and also the resetting of your indicator light.');
    }

	// If tire rotation is selected the short description will be filed with a description of the tire rotation service  
	if(newValue == 'tire rotation'){
     g_form.setValue('short_description', 'Your tires will be rotated and your tire pressure monitor system(TPMS) will be reset as well.');
    }
}
