sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var carService = g_form.getValue('services');

	// Clear all values from the short description field
    g_form.clearValue('price');

	// If brake inspection is selected the price will be filed with the cost of the brake inspection service.  
	if(newValue == 'brake inspection'){
     g_form.setValue('price', '400');
    }

	// If fluid inspection is selected the price will be filed with the cost of the fluid inspection service.  
	if(newValue == 'fluid inspection'){
     g_form.setValue('price', '30');
    }

	// If multi-point inspection is selected the price will be filed with the cost of the multi-point inspection service.  
	if(newValue == 'multi-point inspection'){
     g_form.setValue('price', '100');
    }

	// If oil & filter inspection is selected the price will be filed with the cost of the oil & filter inspection service.  
	if(newValue == 'oil & filter'){
     g_form.setValue('price', '60');
    }

	// If tire rotation is selected the price will be filed with the cost of the tire rotation service.  
	if(newValue == 'tire rotation'){
     g_form.setValue('price', '20');
    }
}
