sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below

    // Allows formats of 9999999999, and no letters to be entered. 
    var maxPrice = /^(\d{1,})$/;

    //An if statement to check to see if the max price number field is in the correct format
    if (!maxPrice.test(newValue)) {
        g_form.setValue('max_price', '');
        g_form.showFieldMsg('max_price', 'Please enter a valid number.', 'error');
    }

    //if statement to make sure that the min price field is lower than the max price field.	
    if (parseInt(g_form.getValue('min_price')) >= parseInt(g_form.getValue('max_price'))) {
        g_form.clearValue('max_price');
        g_form.showFieldMsg('max_price', 'The maximun price cannot be less than or equal to the minimum price.', 'error');
    }
}
