sys_script_include.script:
var Utils = Class.create();
Utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	//Returns an array of sys_ids of cars that are filtered out based upon the selection made by the requester.
    getVehicle: function getVehicle(minPrice, maxPrice, condition) {
        var cars = [];

        var gr = new GlideRecord('x_aaro2_ace_car_inventory');
		// filters cars list based upon the availability 
        gr.addQuery('availability', 'available');
		/* filters car list based upon the min, and max price selected as well as the condition of the car(new/used) 
		and then adds this all to a query.*/
        gr.addQuery('price', '>=', minPrice); // min price
        gr.addQuery('price', '<=', maxPrice); //max price
        gr.addQuery('condition', condition);
        gr.query();

		//This loops through the query while adding the filtered car list to an array and then returns that array list
        while (gr.next()) {

            cars.push(gr.getValue('sys_id'));

            //gs.info(gr.sys_id);

        }
        gs.info(cars);
        return cars;
    },

    type: 'Utils'
});
