sys_script_include.script:
var Utils = Class.create();
Utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    //Returns an array of sys_ids of cars that are filtered out based upon the selection made by the requester.
    getVehicle: function getVehicle(minPrice, maxPrice, condition) {
        var cars = [];

        var gr = new GlideRecord('x_aaro2_ace_car_inventory');
        // filters cars list based upon the availability 
        gr.addQuery('availability', 'available');
        /* filters car list based upon the min, and max price selected as well as the condition of the 
		car(new/used)  and then adds this all to a query.*/
        gr.addQuery('price', '>=', minPrice); // min price
        gr.addQuery('price', '<=', maxPrice); //max price
        gr.addQuery('condition', condition);
        gr.query();

        //This loops through the query while adding the filtered car list to an array and then returns that array list
        while (gr.next()) {

            cars.push(gr.getValue('sys_id'));

            //gs.info(gr.sys_id);

        }
        gs.info(cars);
        return cars;
    },
    // Returns the sys_id of the car selcted along with the price of the car 
    getVehicleInfo: function getVehicleInfo() {
        var vehicleInfo = {};
        //get the value of the parameter passed in from the client-side script.
        var vehicleSysId = this.getParameter('sysparm_vehicle_sysid');
        var vehicle = new GlideRecord('x_aaro2_ace_car_inventory');
        if (vehicle.get(vehicleSysId)) {
            //  gs.info('Ace Car' + vehicle.model);
            vehicleInfo.model = vehicle.getDisplayValue('model');
            vehicleInfo.cost = (parseFloat(vehicle.getValue('price').replace(/,/g, '')).toFixed(2)).toString();
        }
        // gs.info(vehicleInfo.model + " " + vehicleInfo.cost);
        encodedData = JSON.stringify(vehicleInfo);
        // gs.info(encodedData + 'Ace Car Sales');
        return encodedData;
    },
    //returns an array of sys ids of the services selected along with their price
    getServiceInfo: function getServiceInfo() {
        var serviceInfo = [];
        var record = {};
        //get the value of the parameter passed in from the client-side script.
        var serviceSysIds = this.getParameter('sysparm_service_sysids');
        var serviceSysIdArray = serviceSysIds.split(",");
        var service = new GlideRecord('x_aaro2_ace_car_services');

        // gs.info(serviceSysIds + 'ace car ' + typeof serviceSysIds);
        // gs.info(serviceSysIds.length + 'ace car');

        //for loop to loop the and add the selcted servifces to the array
        for (var i = 0; i < serviceSysIdArray.length; i++) {
            //gs.info('ace car' + i + '-- ' + serviceSysIdArray[i]);
            if (service.get(serviceSysIdArray[i])) {
                // gs.info('ace car' + i + '-- ' + service.sys_id);
                record = {
                    'name': service.getDisplayValue('name'),
                    'cost': parseFloat(service.getDisplayValue('price')).toFixed(2)
                };
                //serviceInfo.push(record);
            }
            serviceInfo.push(record);
        }
        // gs.info(serviceInfo[0] + serviceInfo[1]);
        encodedData = JSON.stringify(serviceInfo);
        // gs.info(encodedData + 'Ace Car Sales');
        return encodedData;
    },


    type: 'Utils'
});
